import ComposableArchitecture
import SwiftUI

@ViewAction(for: LogListFeature.self)
public struct LogListView: View {
    public let store: StoreOf<LogListFeature>

    public init(
        store: StoreOf<LogListFeature>
    ) {
        self.store = store
    }

    public var body: some View {
        NavigationStack {
            List(store.logs) { log in
                Button(
                    action: {
                        send(.logTapped(log))
                    },
                    label: {
                        LogListRow(log: log)
                    }
                )
                .buttonStyle(.plain)
            }
            .navigationDestination(
                store: store.scope(
                    state: \.$destination.detail,
                    action: \.destination.detail
                ),
                destination: LogDetailView.init
            )
            .toolbar {
                ToolbarItem(placement: .topBarTrailing) {
                    Button(
                        action: {
                            send(.refreshButtonTapped)
                        },
                        label: {
                            Image(systemName: "arrow.counterclockwise")
                        }
                    )
                }

                ToolbarItem(placement: .topBarTrailing) {
                    ShareLink(
                        item: store.logs,
                        preview: SharePreview("Exported Logs")
                    )
                }
            }
            .navigationTitle("Logs")
        }
        .onAppear { send(.onAppear) }
    }
}

